def test_transformer_anonymization_basic():
    """Test basique d'anonymisation"""

    text = "Marine a appel√© Delphine du cabinet Action Avocats √† Montpellier."

    anonymizer = TransformerAnonymizer()
    anon_text, mapping = anonymizer.anonymize_with_tags(text)

    print(f"\nüîç Test basique")
    print(f"Original: {text}")
    print(f"Anonymis√©: {anon_text}")
    print(f"Mapping: {mapping['entities'].keys()}")

    # V√©rifie que les noms sont anonymis√©s
    assert "Marine" not in anon_text, "Marine devrait √™tre anonymis√©"
    assert "Delphine" not in anon_text, "Delphine devrait √™tre anonymis√©"
    assert "<PERSON_" in anon_text, "Devrait contenir des tags PERSON"

    # V√©rifie la structure du mapping
    assert "entities" in mapping
    assert "reverse_map" in mapping
    assert len(mapping["entities"]) > 0

    # V√©rifie que les espaces sont conserv√©s
    assert "  " not in anon_text, "Pas de double espaces"
    assert " <" in anon_text or "> " in anon_text, "Les espaces autour des tags devraient √™tre conserv√©s"

    # V√©rifie la d√©sanonymisation exacte
    restored = anonymizer.deanonymize(anon_text, mapping)
    print(f"Restaur√©: {restored}")

    # La d√©sanonymisation doit √™tre exacte (ou presque, accepte variations mineures d'espaces)
    assert "Marine" in restored
    assert "Delphine" in restored
    assert "Action Avocats" in restored

    print("‚úÖ Test basique OK")


def test_transformer_with_domain_entities():
    """Test avec entit√©s du domaine"""

    # D√©finis les entit√©s connues de ton domaine (IMPORTANT: inclure les acronymes)
    domain_entities = {
        "PERSON": ["Marine", "Delphine", "Isabelle"],
        "ORGANIZATION": ["Action Avocats", "CJD", "UDAF", "Mille M√©cat"]
    }

    text = ("Marine a rappel√© le client du CJD pour le compte du cabinet Action Avocats. "
            "Delphine devait valider la rupture conventionnelle.")

    anonymizer = TransformerAnonymizer(domain_entities=domain_entities)
    anon_text, mapping = anonymizer.anonymize_with_tags(text)

    print(f"\nüîç Test domaine")
    print(f"Original: {text}")
    print(f"Anonymis√©: {anon_text}")
    print(f"Entit√©s d√©tect√©es: {list(mapping['entities'].keys())}")

    # V√©rifie que toutes les entit√©s du domaine sont d√©tect√©es
    assert "Marine" not in anon_text, "Marine devrait √™tre anonymis√©"
    assert "Delphine" not in anon_text, "Delphine devrait √™tre anonymis√©"
    assert "Action Avocats" not in anon_text, "Action Avocats devrait √™tre anonymis√©"
    assert "CJD" not in anon_text, "CJD devrait √™tre anonymis√©"

    # V√©rifie qu'Action Avocats est bien class√© en ORGANIZATION
    found_org = False
    for tag, info in mapping["entities"].items():
        values_lower = [v.lower() for v in info["values"]]
        if "action avocats" in values_lower:
            assert info["label"] == "ORGANIZATION", f"Action Avocats devrait √™tre ORG, pas {info['label']}"
            found_org = True
        if "cjd" in values_lower:
            assert info["label"] == "ORGANIZATION", f"CJD devrait √™tre ORG, pas {info['label']}"

    assert found_org, "Action Avocats devrait √™tre d√©tect√© comme ORGANIZATION"

    print("‚úÖ Test domaine OK")


