def test_transformer_merges_similar_variants():
    """Les variantes proches doivent √™tre fusionn√©es et corrig√©es."""

    text = "Marine a parl√© √† marines. PENNYLANE rencontre P√©ny L√®ne."
    anonymizer = TransformerAnonymizer()

    marine_start = text.index("Marine")
    marines_start = text.index("marines")
    penny_start = text.index("PENNYLANE")
    peny_start = text.index("P√©ny L√®ne")

    entities = [
        {"start": marine_start, "end": marine_start + len("Marine"), "entity_type": "PERSON", "score": 0.95},
        {"start": marines_start, "end": marines_start + len("marines"), "entity_type": "PERSON", "score": 0.80},
        {"start": penny_start, "end": penny_start + len("PENNYLANE"), "entity_type": "PERSON", "score": 0.90},
        {"start": peny_start, "end": peny_start + len("P√©ny L√®ne"), "entity_type": "PERSON", "score": 0.90},
    ]

    anon_text, mapping = anonymizer.anonymize_with_tags(text, entities=entities)

    person_entities = [info for info in mapping["entities"].values() if info["label"] == "PERSON"]
    assert len(person_entities) == 2, f"Devrait fusionner les variantes, mapping={mapping}"

    canonicals = {info["canonical"] for info in person_entities}
    assert "Marine" in canonicals
    assert any("Peny" in value or "Penny" in value for value in canonicals)
    assert mapping["corrected_text"].count("Marine") == 2
    assert "<PERSON_" in anon_text


def test_transformer_filters_pronouns_and_generic_words():
    """Les mots g√©n√©riques ne doivent pas cr√©er de faux positifs."""

    text = "Elle √©voque une relance, Quand il faut, Moral n'est pas un pr√©nom."
    anonymizer = TransformerAnonymizer()

    entities = []
    for word in ["Elle", "relance", "Quand", "Moral"]:
        start = text.index(word)
        entities.append(
            {"start": start, "end": start + len(word), "entity_type": "PERSON", "score": 0.9}
        )

    _, mapping = anonymizer.anonymize_with_tags(text, entities=entities)

    assert mapping["stats"]["total"] == 0
    assert mapping["entities"] == {}
    assert mapping["corrected_text"] == text


def test_deanonymization_exact():
    """V√©rifie que la d√©sanonymisation est exacte"""

    original = "Marine et Delphine travaillent chez Action Avocats √† Montpellier."

    anonymizer = TransformerAnonymizer(
        domain_entities={
            "PERSON": ["Marine", "Delphine"],
            "ORGANIZATION": ["Action Avocats"]
        }
    )

    anon_text, mapping = anonymizer.anonymize_with_tags(original)
    restored = anonymizer.deanonymize(anon_text, mapping)

    # La d√©sanonymisation doit √™tre exacte
    assert restored == original, f"D√©sanonymisation incorrecte:\nOriginal: {original}\nRestored: {restored}"


